def count(s):
    l = list(s)
    n = 0
    m = 0
    while len(l)>0:
        temp = l.pop(0)
        if temp == '(':
            n = n+1
        elif temp == ')':
            m = m+1
    return n-m


def down(s):
    l = list(s)
    count = 0
    basement = []
    length = len(l) #are you happy
    for i in range(length) :
        if count == -1:
            basement.append(str(i))
        if l[i] == '(':
            count = count+1
        if l[i] == ')':
            count = count-1
    return basement[0]


# Meghan demonstrating that she learned things from Lenny's complaints
def day1(s):
    l = list(s)
    floor = 0
    instruction = 0
    basement = []
    length = len(l)
    for i in range(length):
        if floor == -1:
            basement.append(str(instruction))
        if l[i] == '(':
            floor += 1
        if l[i] == ')':
            floor -= 1
        instruction +=1
    return (floor, basement[0])

